insert into event(name, description, date, eye, img) values("Звездный дождь Персеид","Традиционно самым запоминающимся явлением второй половины лета становится звездный дождь Персеид. Обычная активность данного потока происходит в период с 17 июля по 24 августа. Персеиды являются едва ли не самым динамичным, стабильным, красивым и сильным ежегодным звездопадом. Максимум приходится на 12-13 августа, когда число метеоров достигает 100-110 в час. Для наблюдения метеорного дождя не нужны никакие астрономические приборы, поэтому насладиться ночным зрелищем может любой желающий.В 2013 году главный пик метеорного потока ожидается 12 августа с 18ч до 21ч по Всемирному времени UT. Вблизи максимума Луна будет перед первой четвертью, что создает хорошие условия для ночных наблюдений. Интенсивность этого дождя меняется год от года и зависит от плотности облака частиц в том участке шлейфа кометы, который пересекает Земля.Наблюдать поток лучше всего вдали от городских огней и не слишком лунной ночью, тогда есть шанс увидеть множество более слабых метеоров. Метеоры вылетают из радианта, который легко определить, нанеся на карту несколько метеоров из потока. Место пересечения продолжения путей метеоров от конца к началу и будет точкой радианта R. При наблюдениях метеорных дождей не обязательно смотреть на саму точку радианта. Лучше всего найти стул с откидной спинкой или шезлонг, чтобы удобнее обозревать все небо.",1376326800000,0,"2event.jpg");
